import React from "react";
import axios from "axios";
const HOST = "http://localhost:8080";

// import Login from "./Login";
// import DataManager from "config/api";

class Home extends React.Component {
  constructor(props) {
    super();
    this.state = {
      isLoading: true,
      patients: []
    };
  }
  async componentDidMount() {
    if (!this.props.isAuthenticated) {
      return;
    }

    try {
      const patients = await this.patients();
      this.setState({ patients });
    } catch (e) {
      alert(e);
    }

    this.setState({ isLoading: false });
  }

  patients() {
    const url = HOST + "/admin/patients?apiFlag=true";
    // const response = await axios.get(url);
    // console.log("response", response.data);
    return axios.get(url);
  }
  renderPatientsList(patients) {
    console.log("patients", patients);
    return [{}].concat(patients.data).map((patient, i) =>
      i !== 0 ? (
        <LinkContainer key={patient.id} to={`admin/edit-patient/${patient.id}`}>
          <tbody>
            <tr>
              <td>{i}</td>
              <td>{patient.name}</td>
              <td>{patient.age}</td>
              <td>{patient.gender}</td>
              <td>{patient.walletAmount}</td>
            </tr>
          </tbody>
        </LinkContainer>
      ) : (
          <LinkContainer key="new" to="/admin/add-patient">
            <tbody>
              <tr>
                <td>
                  <b>{"\uFF0B"}</b>Add a new patient
              </td>
              </tr>
            </tbody>
          </LinkContainer>
        )
    );
  }

  renderLander() {
    return (
      <section id="home">      
  <video id="home-bg-video" poster="video/kiravi.jpg" autoplay loop muted>
          <source src="video/solo.mp4" type="video/mp4" />
          <source src="video/solo.ogv" type="video/ogg" />
          <source src="video/solo.webm" type="video/webm" />
        </video>

  <div id="home-overlay"></div>

  <div id="home-content">
          <div id="home-content-inner" class="text-center">
            <div id="home-heading">
              <h1 id="home-heading-1">It's Your Life</h1>
              <br />
              <h1 id="home-heading-2">Take control <span>Now</span></h1>
            </div>
            <div id="home-text">
              <p>
                “If the plan doesn’t work, change the plan, but never the goal.”
        </p>
            </div>
            <div id="home-btn">
              <a
                class="btn btn-general btn-home"
                href="#about"
                title="Start Now"
                role="button"
              >Start Now</a
              >
            </div>
          </div>
        </div>

  <a href="#about" id="arrow-down" class="smooth-scroll">
          <i class="fa fa-angle-down"></i>
        </a>
      </section>
    );
  }

  renderPatients() {
    return (
      <div className="patients">
        <Card>
          <Card.Header>List of Patients</Card.Header>
          <Card.Body>
            <Table className="patientTable" striped bordered hover responsive>
              <thead>
                <tr>
                  <th>No.</th>
                  <th>Name</th>
                  <th>Age</th>
                  <th>Gender</th>
                  <th>Wallet</th>
                </tr>
              </thead>
              {!this.state.isLoading &&
                this.renderPatientsList(this.state.patients)}
            </Table>
          </Card.Body>
        </Card>
      </div>
    );
  }
  render() {
    return (
      <div className="Home">
        {this.props.isAuthenticated
          ? this.renderPatients()
          : this.renderLander()}
      </div>
    );
  }
}

export default Home;
