import React from "react";
import axios from "axios";
const HOST = "http://localhost:8080";

// import Login from "./Login";
// import DataManager from "config/api";

class Home extends React.Component {
  constructor(props) {
    super();
    this.state = {
      isLoading: true,
      patients: []
    };
  }
  async componentDidMount() {
    if (!this.props.isAuthenticated) {
      return;
    }

    try {
      const patients = await this.patients();
      this.setState({ patients });
    } catch (e) {
      alert(e);
    }

    this.setState({ isLoading: false });
  }

  patients() {
    const url = HOST + "/admin/patients?apiFlag=true";
    // const response = await axios.get(url);
    // console.log("response", response.data);
    return axios.get(url);
  }
  renderPatientsList(patients) {
    console.log("patients", patients);
    return [{}].concat(patients.data).map((patient, i) =>
      i !== 0 ? (
        <LinkContainer key={patient.id} to={`admin/edit-patient/${patient.id}`}>
          <tbody>
            <tr>
              <td>{i}</td>
              <td>{patient.name}</td>
              <td>{patient.age}</td>
              <td>{patient.gender}</td>
              <td>{patient.walletAmount}</td>
            </tr>
          </tbody>
        </LinkContainer>
      ) : (
          <LinkContainer key="new" to="/admin/add-patient">
            <tbody>
              <tr>
                <td>
                  <b>{"\uFF0B"}</b>Add a new patient
              </td>
              </tr>
            </tbody>
          </LinkContainer>
        )
    );
  }

  renderLander() {
    return (
      <div className="lander">
        <Card>
          <Card.Header>Online Hospital</Card.Header>
          <Card.Body>
            <Card.Title>A simple patients consultations app</Card.Title>
            <Card.Text>Welcome to this hospital!</Card.Text>
          </Card.Body>
        </Card>
      </div>
    );
  }

  renderPatients() {
    return (
      <div className="patients">
        <Card>
          <Card.Header>List of Patients</Card.Header>
          <Card.Body>
            <Table className="patientTable" striped bordered hover responsive>
              <thead>
                <tr>
                  <th>No.</th>
                  <th>Name</th>
                  <th>Age</th>
                  <th>Gender</th>
                  <th>Wallet</th>
                </tr>
              </thead>
              {!this.state.isLoading &&
                this.renderPatientsList(this.state.patients)}
            </Table>
          </Card.Body>
        </Card>
      </div>
    );
  }
  render() {
    return (
      <div className="Home">
        {this.props.isAuthenticated
          ? this.renderPatients()
          : this.renderLander()}
      </div>
    );
  }
}

export default Home;
